class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long sz=(m-1)+(n-1);
        vector<pair<long long,long long>> c;
        for(int i=0;i<m-1;i++){
            c.push_back({1ll*hc[i],0ll});
        }
        for(int i=0;i<n-1;i++){
            c.push_back({1ll*vc[i],1ll});
        }
        sort(c.rbegin(),c.rend());
        long long ph=1;
        long long pv=1;
        long long ans=0;
        for(int i=0;i<sz;i++){
            long long cost=c[i].first;
            long long type=c[i].second;
            if(type==0){
                ans+=(pv*cost);
                ph++;
            }else{
                ans+=(ph*cost);
                pv++;
            }
        }
        return ans;
    }
};
