/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function(m, n, horizontalCut, verticalCut) {
  horizontalCut.sort((a, b) => b - a);
  verticalCut.sort((a, b) => b - a);

  let hIndex = 0;
  let vIndex = 0;
  let hPieces = 1;
  let vPieces = 1;
  let totalCost = 0;

  while (hIndex < horizontalCut.length && vIndex < verticalCut.length) {
    if (horizontalCut[hIndex] >= verticalCut[vIndex]) {
      totalCost += horizontalCut[hIndex] * vPieces;
      hPieces++;
      hIndex++;
    } else {
      totalCost += verticalCut[vIndex] * hPieces;
      vPieces++;
      vIndex++;
    }
  }

  // If there are remaining horizontal cuts
  while (hIndex < horizontalCut.length) {
    totalCost += horizontalCut[hIndex] * vPieces;
    hPieces++;
    hIndex++;
  }

  // If there are remaining vertical cuts
  while (vIndex < verticalCut.length) {
    totalCost += verticalCut[vIndex] * hPieces;
    vPieces++;
    vIndex++;
  }

  return totalCost; 
};
