class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hp,vp = 1,1
        ac = []
        ans = 0
        for i in horizontalCut:
            ac.append([i,0])
        for i in verticalCut:
            ac.append([i,1])
        ac.sort(reverse = True)
        for i in ac:
            if i[1] == 0:
                ans+=vp*i[0]
                hp+=1
            else:
                ans+=hp*i[0]
                vp+=1
        return ans
