class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(vc.rbegin(), vc.rend());
        sort(hc.rbegin(), hc.rend());
        int vc_cnt = 0;
        int hc_cnt = 0;
        long long cost = 0;
        for (int i = 1; i < m + n -1; ++i) {
            auto cost_val = 0;
            if (vc_cnt == vc.size()) {
                cost += (long long)hc[hc_cnt++] * (vc_cnt + 1);
                continue;
            }
            if (hc_cnt == hc.size()) {
                cost += (long long)vc[vc_cnt++] * (hc_cnt + 1);
                continue;
            }
            if (vc[vc_cnt] > hc[hc_cnt]) {
                cost += (long long)vc[vc_cnt++] * (hc_cnt + 1);
            } else {
                cost += (long long)hc[hc_cnt++] * (vc_cnt + 1);
            }
        }
        return cost;
    }
};
