class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        hpieces = 1
        vpieces = 1

        hp = m - 2
        np = n - 2
        cost = 0
        while hp >= 0 and np >= 0:
            if horizontalCut[hp] > verticalCut[np]:
                cost += horizontalCut[hp] * vpieces
                hpieces += 1
                hp -= 1
            else:
                cost += verticalCut[np] * hpieces
                vpieces += 1
                np -= 1

        if hp < 0:
            while np >= 0:
                cost += verticalCut[np] * hpieces
                vpieces += 1
                np -= 1
        elif np < 0:
            while hp >= 0:
                cost += horizontalCut[hp] * vpieces
                hpieces += 1
                hp -= 1

        return cost
