class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& A, vector<int>& B) {
      int64_t ans = 0;
      sort(A.begin(), A.end());
      sort(B.begin(), B.end());
      int ia = 0, ib = 0;
      int ca = 1, cb = 1;
      while (A.size() && B.size()) {
        if (A.back()>B.back())  ans += A.back()*cb, ca++, A.pop_back();
        else ans += B.back()*ca, cb++, B.pop_back();
      }
      while (A.size())  ans += A.back()*cb, ca++, A.pop_back();
      while (B.size())  ans += B.back()*ca, cb++, B.pop_back();
      return ans;
    }
};
