class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0ll;
        int a=0,b=0;
        sort(h.begin(),h.end(), greater<int>());
        sort(v.begin(),v.end(), greater<int>());
        while(a<m-1&&b<n-1){
            // c1=h[a]*(b+1);
            // c2=v[b]*(a+1);
            if(h[a]>v[b]||(h[a]==v[b]&&a>=b)){
                ans+=h[a]*(b+1);
                ++a;
            }else{
                ans+=v[b]*(a+1);
                ++b;
            }
        }
        while(a<m-1){
            ans+=h[a]*(b+1);
            ++a;
        }
        while(b<n-1){
            ans+=v[b]*(a+1);
            ++b;
        }
        return ans;

    }
};
