class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        vector<pair<long long,pair<long long,long long>>>res;
        for(int i=0;i<hCut.size();i++){
            res.push_back({hCut[i],{0,i}});
        }
        for(int i=0;i<vCut.size();i++){
            res.push_back({vCut[i],{1,i}});
        }
        sort(res.begin(),res.end());
        reverse(res.begin(),res.end());
        long long hpieces=1;
        long long vpieces=1;
        long long cost=0;
        for(auto it:res){
            if(it.second.first==0){
                cost+=vpieces*it.first;
                hpieces++;
            }
            else{
            cost+=hpieces*it.first;
            vpieces++;
            }

        }
        return cost;
    }
};
