class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {

        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());

        long long h=m, v=n;
        long long hcut=1, vcut=1, cost=0, i=m-2, j=n-2;

        while(i>=0 && j>=0)
        {            
            if(hc[i]>vc[j])
            {
                cost+=vcut*hc[i];
                hcut++;
                i--;
            }

            else
            {
                cost+=hcut*vc[j];
                vcut++;
                j--;
            }
        }

        while(i>=0)
        {
            cost+=vcut*hc[i];
            hcut++;
            i--;
        }

        while(j>=0)
        {
            cost+=hcut*vc[j];
            vcut++;
            j--;
        }

        return cost;        
    }
};
