class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>>a;
        for (int i=0;i<h.size();i++){
            a.push_back({h[i],0});
        }
        for (int i=0;i<v.size();i++){
            a.push_back({v[i],1});
        }
        sort(a.begin(),a.end());
        long long ans=0;
        long long cnt=0;
        long long cnt2=0;
        for (int i=a.size()-1;i>=0;i--){
            if (a[i].second==0){
                ans+=(cnt+1)*a[i].first;
                cnt2++;

            }
            else{
                ans+=(cnt2+1)*a[i].first;
                cnt++;    

            }


        }
        return ans;

    }
};
