class Solution {
public:
    using LL=long long;
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) 
    {
        LL ans=0;
        LL row=0,col=0;
        vector<pair<LL,LL>> vec;
        vector<int>& h=horizontalCut,&v=verticalCut;
        for(auto &c:h)
        {
            vec.emplace_back(c,0);
        }

        for(auto &c:v)
        {
            vec.emplace_back(c,1);
        }

        sort(vec.rbegin(),vec.rend());

        for(int i=0;i<vec.size();++i)
        {
            auto [x,ch]=vec[i];
            if(ch==0)
            {
                ans+=(col+1)*x;
                ++row;
            }
            else
            {
                ans+=(row+1)*x;
                ++col;
            }
        }


        return ans;
    }
};
