class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());
        int hids = 0, vids = 0;
        int hPieces = 1, vPieces = 1;
        long long ans = 0;

        while (hids < horizontalCut.size() && vids < verticalCut.size()) {
            if (horizontalCut[hids] > verticalCut[vids]) {

                ans += horizontalCut[hids] * vPieces;
                hPieces++;
                hids++;
            } else {

                ans += verticalCut[vids] * hPieces;
                vPieces++;
                vids++;
            }
        }

        while (hids < horizontalCut.size()) {
            ans += horizontalCut[hids] * vPieces;
            hids++;
        }

        while (vids < verticalCut.size()) {
            ans += verticalCut[vids] * hPieces;
            vids++;
        }

        return ans;
    }
};
