class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        h.push_back(-100);
        v.push_back(-100);
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        long long i = 0, j = 0;
        long long r = 1, c = 1;
        while(i+1 < (int)h.size() || j+1 < (int)v.size()){
            if(h[i] >= v[j]){
                long long cur = h[i]*c;
                ans += cur;
                r++;
                i++;
            }
            else{
                long long cur = v[j]*r;
                ans += cur;
                c++;
                j++;
            }
        }
        return ans;
    }
};
