class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        for(int i = 0; i<m-1; i++) h[i]*=-1;
        for(int i = 0; i<n-1; i++) v[i]*=-1;
        Arrays.sort(h);
        Arrays.sort(v);
        for(int i = 0; i<m-1; i++) h[i]*=-1;
        for(int i = 0; i<n-1; i++) v[i]*=-1;
        int p1 = 0, p2 = 0;
        long ans = 0;
        long a = 1, b = 1;
        while(p1<m-1 || p2<n-1){
            if(p2 == n-1 || (p1 != m-1 && h[p1]>v[p2])){
                ans+=h[p1]*b;
                a++;p1++;
            }else{
                ans+=v[p2]*a;
                b++;p2++;
            }
        }
        return ans;
    }
}
