#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        // Sort the cuts in descending order
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());

        long long hPieces = 1, vPieces = 1;
        long long totalCost = 0;

        int hIndex = 0, vIndex = 0;

        // Iterate through both sorted cut arrays
        while (hIndex < horizontalCut.size() && vIndex < verticalCut.size()) {
            if (horizontalCut[hIndex] >= verticalCut[vIndex]) {
                totalCost += static_cast<long long>(horizontalCut[hIndex]) * vPieces;
                hPieces++;
                hIndex++;
            } else {
                totalCost += static_cast<long long>(verticalCut[vIndex]) * hPieces;
                vPieces++;
                vIndex++;
            }
        }

        // Process remaining horizontal cuts, if any
        while (hIndex < horizontalCut.size()) {
            totalCost += static_cast<long long>(horizontalCut[hIndex]) * vPieces;
            hPieces++;
            hIndex++;
        }

        // Process remaining vertical cuts, if any
        while (vIndex < verticalCut.size()) {
            totalCost += static_cast<long long>(verticalCut[vIndex]) * hPieces;
            vPieces++;
            vIndex++;
        }

        return totalCost;
    }
};
