class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<vector<int>> v;
        for(int i=0;i<n-1;i++){
            vector<int> temp(2);
            temp[0]=vc[i];
            temp[1]=1;
            v.push_back(temp);
        }
        for(int i=0;i<m-1;i++){
            vector<int> temp(2);
            temp[0]=hc[i];
            temp[1]=0;
            v.push_back(temp);
        }
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        int hnum=1,vnum=1;
        long long cost=0;

        for(auto it: v){
            int curr_cost= it[0];
            int type=it[1];
            // cout<<it[0]<<it[1]<<endl;
            if(type==1){
                cost+=(curr_cost*hnum);
                vnum++;
            }
            else{
                cost+=(curr_cost*vnum);
                hnum++;
            }
        }
        return cost;

    }
};
