#define lln long long int

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& htc, vector<int>& vtc) {

        sort(htc.rbegin(), htc.rend());

        sort(vtc.rbegin(), vtc.rend());

        lln ans = 0,hs=1,vs=1,x=0,y=0;

        while (x<htc.size() && y<vtc.size()) {
            if (htc[x]>vtc[y]) {
                ans+= 1ll*htc[x]*vs;
                hs++;
                x++;
            } else {
                ans+= 1ll*vtc[y]*hs;
                vs++;
                y++;
            }
        }

        while (x<htc.size()) {
            ans += 1ll*htc[x]*vs;
            hs++;
            x++;
        }

        while (y<vtc.size()) {
            ans += 1ll*vtc[y]*hs;
            vs++;
            y++;
        }

        return ans;

    }
};
