class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        int horizontalparts = 1;
        int verticalparts = 1;
        long cost = 0 ;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        int i = horizontalCut.length-1;
        int j = verticalCut.length -1;

        while (i>=0 && j>=0){
            if(i>=0 && j >=0){
                if(horizontalCut[i] >= verticalCut[j]){
                    cost += horizontalCut[i]*verticalparts;
                    horizontalparts++;
                    i--;
                }
                else {
                    cost += verticalCut[j]*horizontalparts;
                    verticalparts++;
                    j--;
                }
            }
        }
        while (i>=0 && j<0){
                    cost += horizontalCut[i]*verticalparts;
                    horizontalparts++;
                    i--;
        }
        while (j>=0 && i<0){
                    cost += verticalCut[j]*horizontalparts;
                    verticalparts++;
                    j--;
        }
        return cost;
    }
}
