class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.rbegin(),hCut.rend());
        sort(vCut.rbegin(),vCut.rend());
        int len1=hCut.size(),len2=vCut.size();
        int i=0,j=0,r=0,c=0;
        long long res=0;
        for(int x=0;x<len1+len2 && i < len1 && j<len2;x++)
        {
            if( hCut[i]>=vCut[j]){
                res = res +(hCut[i]*(c+1));
                r++;
                i++;
            }
            else 
            {
                res = res + (vCut[j]*(r+1));
                c++;
                j++;
            }
        }
            while(j<len2)
            {
                res = res + (vCut[j]*(r+1));
                j++;
                c++;
            }
            while(i<len1)
            {
                res = res + (hCut[i]*(c+1));
                i++;
                r++;
            }
        return res;



    }
};
