class Solution {
public:
    long long minimumCost(int a, int b, vector<int>& H, vector<int>& V) {
        set<int>st;
        vector<pair<int, int>> p;
        for (int it : V) {
            p.push_back({it, 1});
        }
        for (int c : H) {
            p.push_back({c, 0});
        }
        sort(p.begin(), p.end(), greater<pair<int, int>>());
        long long sum = 0, hp = 1,vp = 1; 
        for (auto& cut : p) {
            int paisa = cut.first, t = cut.second;       
            if (t == 0) {
                sum += paisa * vp;
                hp++;
            } else {
                sum += paisa * hp;
                vp++;
            }
        }

        return sum;
    }
};
