class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>>g;
        n--,m--;
        for(int i=0;i<n;i++) g.push_back({h[i],0});
        for(int i=0;i<m;i++) g.push_back({v[i],1});
        sort(g.begin(),g.end(),greater<pair<int,int>>());
        long long hp=1,vp=1,ans=0;
        for(int i=0;i<n+m;i++) {
            int c=g[i].first,t=g[i].second;
            if(t) {
                ans+=c*vp;
                hp++;
            } else {
                ans+=c*hp;
                vp++;
            }
            //cout << c << " " << t << " " <<  ans << " " << hp << " " << vp << "\n";
        }
        return ans;
    }
};
