class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& vecr1, vector<int>& vecr2) {
        long long i = vecr1.size() - 1;
        long long j = vecr2.size() - 1;
        long long res = 0;
        sort(vecr1.begin(), vecr1.end());
        sort(vecr2.begin(), vecr2.end());
        long long xpcs = 1, ypcs = 1;
        while (!(i < 0 || j < 0)) {
            if (vecr1[i] <= vecr2[j]) {
                res += 1ll*vecr2[j] * xpcs;
                ypcs++;
                j--;
            } else {
                res += 1ll*vecr1[i] * ypcs;
                xpcs++;
                i--;
            }
        }
        while (!(i < 0)) {
            res += 1ll* vecr1[i] * ypcs;
            i--;
        }
        while (!(j < 0)) {
            res += 1ll* xpcs*vecr2[j];
            j--;
        }

        return res;
    }
};
