class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
            c = []
            for i in range(m - 1):
                c.append((-horizontalCut[i], 'h'))
            for j in range(n - 1):
                c.append((-verticalCut[j], 'v'))
            c.sort()

            heap = []

            rows, cols = 1, 1

            ans = 0
            for cost, cut_type in c:
                if cut_type == 'h':
                    ans += -cost * cols
                    rows += 1
                else:
                    ans += -cost * rows
                    cols += 1
            return ans
