class Solution {
public:

    long long solve(int n, const vector<int>& a) {
		long long res = 0;
		for (int x : a) res += x;
		return res;
    }
  long long minimumCost(int m, int n, vector<int> &h, vector<int> &v) {
	  sort(h.rbegin(), h.rend());
	  sort(v.rbegin(), v.rend());
	  int cnth = 0, cntv = 0;
	  int ptr1 = 0, ptr2 = 0;
	  long long ans = 0;
	  for (int i = 0; i < h.size() + v.size(); ++i) {
		  if (ptr1 < h.size() && (ptr2 == v.size() || h[ptr1] > v[ptr2])) {
			  ans += (ptr2 + 1) * h[ptr1++];
		  }
		  else {
			  ans += (ptr1 + 1) * v[ptr2++];
		  }
	  }
	  return ans;
  }
};
