class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int,int>>pq;
        for(auto ele : horizontalCut)pq.push({ele,0});
        for(auto ele : verticalCut)pq.push({ele,1});
        long long ans=0,hc=1,vc=1;
        while(pq.size()){
            int ele=pq.top().first,f=pq.top().second;pq.pop();
            if(f==0){ans=(ans+ele*vc);hc++;}
            else{ans=(ans+ele*hc);vc++;}
        }
        return ans;
    }
};
