struct Cut {
    int cost;
    char type; 

    Cut(int c, char t) : cost(c), type(t) {}
};

bool compareCuts(const Cut& a, const Cut& b) {
    return a.cost > b.cost;
}

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<Cut> cuts;

        for (int i = 0; i < m - 1; ++i) {
            cuts.emplace_back(horizontalCut[i], 'H');
        }
        for (int j = 0; j < n - 1; ++j) {
            cuts.emplace_back(verticalCut[j], 'V');
        }

        sort(cuts.begin(), cuts.end(), compareCuts);

        int horizontalPieces = 1;
        int verticalPieces = 1;
        long long totalCost = 0;

        for (const auto& cut : cuts) {
            if (cut.type == 'H') {
                totalCost += static_cast<long long>(cut.cost) * verticalPieces;
                ++horizontalPieces;
            } else {
                totalCost += static_cast<long long>(cut.cost) * horizontalPieces;
                ++verticalPieces;
            }
        }

        return totalCost;
    }
};
