#include <vector>
#include <deque>
#include <algorithm>

#define ll long long

using namespace std;

class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        deque<ll> hq(hc.begin(), hc.end()), vq(vc.begin(), vc.end());
        sort(hq.begin(), hq.end(), greater<ll>());
        sort(vq.begin(), vq.end(), greater<ll>());

        ll hp = 1, vp = 1;
        ll tc = 0;

        while (!hq.empty() && !vq.empty()) {
            tc += (hq.front() >= vq.front()) ? (hq.front() * vp) : (vq.front() * hp);
            (hq.front() >= vq.front()) ? (hp++, hq.pop_front()) : (vp++, vq.pop_front());
        }

        while (!hq.empty()) {
            tc += hq.front() * vp;
            hq.pop_front();
        }

        while (!vq.empty()) {
            tc += vq.front() * hp;
            vq.pop_front();
        }

        return tc;
    }
};
