class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end()); reverse(hc.begin(), hc.end());
        sort(vc.begin(), vc.end()); reverse(vc.begin(), vc.end());
        long long i=0, j=0, hp= 1, vp=1, ans=0;
        while(i<hc.size() && j<vc.size()){
            if(hc[i]>vc[j]) ans+=hc[i]*vp, hp++, i++;
            else ans+=vc[j]*hp, vp++, j++;
        } while(i<hc.size()) ans+=hc[i]*vp, i++;
        while(j<vc.size()) ans+=vc[j]*hp, j++;
        return ans;
    }
};
