class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        priority_queue<pair<int,int>> q;
        for(int i=0;i<hor.size();i++){
            q.push({hor[i],0});
        }
        for(int i=0;i<ver.size();i++){
            q.push({ver[i],1});
        }
        long long ans=0;
        int h=1;
        int v=1;
        while(!q.empty()){
            int n=q.top().first;
            int c=q.top().second;
            if(c==0){
                ans+=v*n;
                h++;
            }
            else{
                ans+=h*n;
                v++;
            }

            q.pop();
        }
        return ans;
    }
};
