class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int hcuts = 1;
        int vcuts = 1;
        std::sort(horizontalCut.begin(), horizontalCut.end());
        std::sort(verticalCut.begin(), verticalCut.end());
        long long cost = 0;
        int i = horizontalCut.size() - 1;
        int j = verticalCut.size() - 1;
        while (i >= 0 || j >= 0) {
            if (i < 0) {
                cost += verticalCut[j] * hcuts;
                vcuts++;
                j--;
            } else if (j < 0) {
                cost += horizontalCut[i] * vcuts;
                hcuts++;
                i--;
            } else if (horizontalCut[i] > verticalCut[j]) {
                cost += horizontalCut[i] * vcuts;
                hcuts++;
                i--;
            } else {
                cost += verticalCut[j] * hcuts;
                vcuts++;
                j--;
            }
        }
        return cost;
    }
};
