class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut); 
        Arrays.sort(verticalCut);
        long hsum = 0,vsum  = 0;
        for(int i:horizontalCut)hsum += i;
        for(int j:verticalCut)vsum += j;
        long ans = 0;
        for(int i=m-2,j=n-2,ii=1,jj=1;!(i < 0 && j < 0);){
            if(i < 0){
                ans += (vsum * jj);
                break;
            }
            if(j < 0){
                ans += (hsum * ii);
                break;
            }
            if(horizontalCut[i] > verticalCut[j]){
                ans += (horizontalCut[i]*ii);
                hsum -= horizontalCut[i];
                jj++;
                i--;
            }else{
                ans += (verticalCut[j]*jj);
                vsum -= verticalCut[j];
                ii++;
                j--;
            }
        }

        return ans;
    }
}
