class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        vector<pair<long long, char>>x;
        for(int i=0;i<a.size();i++){
            x.push_back({1LL*a[i], 'H'});}
         for(int i=0;i<b.size();i++){
            x.push_back({1LL*b[i], 'V'});}
        sort(x.rbegin(), x.rend());

        long long h = 1, v = 1;
        long long ans = 0;
        for(int i=0;i<x.size();i++){
            if (x[i].second == 'H') {
                ans += x[i].first*1LL*v;
                h++;}
            else {
                ans += x[i].first *1LL* h;
                v++;}}
        return ans;


    }
};
