class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        heap = []
        for idx, h_cut in enumerate(horizontalCut):
            heapq.heappush(heap, (-h_cut,'h', idx))
        for idx, v_cut in enumerate(verticalCut):
            heapq.heappush(heap, (-v_cut,'v', idx))

        h_multi = 1
        v_multi = 1
        ans = 0
        while heap:
            val, _type, idx = heapq.heappop(heap)
            if _type == 'v':
                ans += val*v_multi
                h_multi+=1
            else:
                ans += val*h_multi
                v_multi+=1
        return ans*-1
