class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());

        vector<long> presumh(m, 0), presumv(n, 0);
        for (int i = 0; i < m - 1; ++i) {
            presumh[i+1] = presumh[i] + hc[i];
        }
        for (int i = 0; i < n - 1; ++i) {
            presumv[i+1] = presumv[i] + vc[i];
        }

        int i = m-2, j = n-2;
        long res = 0;
        while (i >= 0 && j >= 0) {
            if (hc[i] > vc[j]) {
                res += (long)hc[i] * (n - 1 - j);
                i--;
            } else if (hc[i] < vc[j]) {
                res += (long)vc[j] * (m - 1 - i);
                j--;
            } else {
                if (presumh[i] > presumv[j]) {
                    res += (long)hc[i] * (n - 1 - j);
                    i--;
                } else if (presumh[i] < presumv[j]) {
                    res += (long)vc[j] * (m - 1 - i);
                    j--;
                } else if (i <= j) {
                    res += (long)hc[i] * (n - 1 - j);
                    i--;
                } else {
                    res += (long)vc[j] * (m - 1 - i);
                    j--;
                }
            }
        }
        while (i >= 0) {
            res += (long)hc[i--] * n;
        }
        while (j >= 0) {
            res += (long)vc[j--] * m;
        }
        return res;
    }
};
