class Solution:
    def minimumCost(self, n: int, m: int, A: List[int], B: List[int]) -> int:
        edges = []
        for i in A:
            edges.append([i, 0])
        for i in B:
            edges.append([i, 1])
        edges.sort()
        ans = 0
        for i in edges:
            if i[1] == 0:
                ans += m * i[0]
                n -= 1
            else:
                ans += n * i[0]
                m -= 1
        return ans
