class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {

        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        long long ans = 0;
        int h1 = 1;
        int v1 = 1;
        int i = 0, j = 0;

        while (i < h.size() || j < v.size()) {
            if (j == v.size() || (i < h.size() && h[i] >= v[j])) {
                ans += h[i] * v1;
                h1++;
                i++;
            } else {
                ans += v[j] * h1;
                v1++;
                j++;
            }
        }

        return ans;
    }
};
