class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        out = 0
        ls = []
        horizon, vert = 1, 1
        for i in horizontalCut:
            ls.append([i, True])
        for i in verticalCut:
            ls.append([i, False])
        ls.sort(reverse=True)
        for i in ls:
            if i[1] == True:
                horizon += 1
                out += vert*i[0]
            else:
                vert += 1
                out += horizon*i[0]
        return out
