class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pair<int,int>> v;
        for(int i=0;i<m-1;i++) v.push_back({hc[i],1});
        for(int i=0;i<n-1;i++) v.push_back({vc[i],0});
        sort(v.rbegin(), v.rend());
        long long hparts = 1, vparts = 1;
        long long ans = 0;
        for(int i=0; i<v.size(); i++) {
            if(v[i].second==1) {
                ans+=vparts*v[i].first;
                hparts+=1;
            }
            else {
                ans+=hparts*v[i].first;
                vparts+=1;
            }
        }
        return ans;
    }
};
