class Solution {
public:
    int static compare(pair<int,int> &c,pair<int,int> &d){
        if(c.first>d.first){
            return 1;
        }
        return 0;
    }
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> v;
        for(auto it : horizontalCut){
            v.push_back({it,1});
        }
        for(auto it : verticalCut){
            v.push_back({it,0});
        }
        sort(v.begin(),v.end(),compare);
        int vt=0;
        int h=0;
        long long int cost=0;
        for(int i=0;i<v.size();i++){
            if(v[i].second==1){
                cost=cost+v[i].first*(vt+1);
                h++;
            }
            else if(v[i].second==0){
                cost=cost+v[i].first*(h+1);
                vt++;
            }
        }
        return cost;

    }
};
