#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(int kush, int n, vector<int>& raj, vector<int>& kusharr) {

        sort(raj.rbegin(), raj.rend());
        sort(kusharr.rbegin(), kusharr.rend());

        long long total_cost = 0;
        int h_segments = 1, v_segments = 1;
        int h_index = 0, v_index = 0;

        while (h_index < raj.size() && v_index < kusharr.size()) {
            if (raj[h_index] >= kusharr[v_index]) {
                total_cost += (long long)raj[h_index] * v_segments;
                h_segments++;
                h_index++;
            } else {
                total_cost += (long long)kusharr[v_index] * h_segments;
                v_segments++;
                v_index++;
            }
        }

        while (h_index < raj.size()) {
            total_cost += (long long)raj[h_index] * v_segments;
            h_segments++;
            h_index++;
        }

        while (v_index < kusharr.size()) {
            total_cost += (long long)kusharr[v_index] * h_segments;
            v_segments++;
            v_index++;
        }

        return total_cost;
    }
};
