class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,char>>vec;
        for(int i=0;i<m-1;i++){
            vec.push_back({horizontalCut[i],'H'});
        }
        for(int i=0;i<n-1;i++){
            vec.push_back({verticalCut[i],'V'});
        }
        sort(vec.begin(),vec.end(),greater<pair<int,char>>());
        long long h=1,v=1;
        long long ans=0;
        for(auto &it : vec){
            if(it.second=='V'){
                ans+=it.first*h;
                v++;
            }
            else{
                ans+=it.first*v;
                h++;
            }
        }
        return ans;

    }
};
