class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int hp = 1;  
        int vp = 1;  
        long long ans = 0;
        priority_queue<pair<int, char>> pq;
        for (int cost : h) pq.push({cost, 'H'});
        for (int cost : v) pq.push({cost, 'V'});
        while (!pq.empty()) {
            int cost = pq.top().first;
            int dir = pq.top().second;
            pq.pop();
            if (dir == 'H') {
                ans += cost * vp;
                hp++; 
            } else {
                ans += cost * hp;
                vp++; 
            }
        }

        return ans;
    }
};
