class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, int> > cuts;
        for(int h: horizontalCut) {
            cuts.push_back({h, 0});
        }
        for(int v: verticalCut) {
            cuts.push_back({v, 1});
        }
        sort(cuts.rbegin(), cuts.rend());
        long long h = 0;
        long long v = 0;
        long long ans = 0;
        for(pair<int, int> c: cuts) {
            if(c.second == 0) {
                ans += c.first * (v + 1);
                h++;
            } else {
                ans += c.first * (h + 1);
                v++;
            }
        }
        return ans;
    }
};
