class Solution {
    public long minimumCost(int m, int n, int[] hc, int[] vc) {
        Arrays.sort(hc);
        Arrays.sort(vc);
        long sum  = 0;
        int a = 0,b = 0;
        while(a < m-1 || b < n-1){
            int hmax = a < m-1 ? hc[m - 2 - a] : 0,vmax = b < n-1 ? vc[n - 2 - b] : 0;
            if(hmax > vmax  || b >= n){
                sum += hmax * (b+1);
                a++;
            }else if(hmax <= vmax  || a >= m){
                sum += vmax * (a+1);
                b++;
            }
        }
        return sum;
    }
}
