class Solution {
public:
    long long ans = 0;
    int lh=0,lv=0,hp=1,vp=1;
    long long minimumCost(int m, int n, std::vector<int>& numh, std::vector<int>& numv) {
        sort(numh.begin(), numh.end(), greater<int>());
        sort(numv.begin(), numv.end(), greater<int>());
        while (lh < numh.size() && lv < numv.size()) {
            if (numh[lh] > numv[lv]) {
                ans += static_cast<long long>(numh[lh]) * vp;
                hp++;
                lh++;
            }else{
                ans+=static_cast<long long>(numv[lv])*hp;
                vp++;
                lv++;
            }
        }
        while(lh<numh.size()) {
            ans+=static_cast<long long>(numh[lh]) * vp;
            lh++;
        }
        while(lv<numv.size()) {
            ans+=static_cast<long long>(numv[lv]) * hp;
            lv++;
        }
        return ans;
    }
};
