#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue<pair<ll,char>> ms;
        ll vp=1,hp=1;
        for(int i=0;i<m - 1;i++){
            ms.push({hc[i],'h'});
        }
        for(int i=0;i<n - 1;i++){
            ms.push({vc[i],'v'});
        }
        ll cst = 0;
        while(!ms.empty()){
            pair<ll,char> p = ms.top();
            ms.pop();
            char c = p.second;
            ll a = p.first;
            if(c=='v'){
                cst += a*hp;
                vp++;
            }
            else{
                cst += a*vp;
                hp++;
            }
        }
        return cst;
    }
};
