class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        arr = []

        for hor in horizontalCut:
            arr.append([hor,'H'])

        for ver in verticalCut:
            arr.append([ver,'V'])

        arr.sort(key = lambda x:(-x[0]))

        ans = 0
        verMul = 1
        horMul = 1

        for i in range(len(arr)):
            if arr[i][1]=='V':
                ans += verMul*arr[i][0]
                horMul+=1
            else:
                ans += horMul*arr[i][0]
                verMul+=1

        return ans
