class Solution {
    public long minimumCost(int m, int n, int[] hor, int[] ver) {
        Arrays.sort(hor);
        Arrays.sort(ver);
        long hp = 1;
        long vp = 1;
        int i = hor.length-1;
        int j = ver.length-1;
        long ans = 0;
        while(i >= 0 && j >= 0){
            if(hor[i] > ver[j]){
                ans += (long)(hor[i])*hp;
                vp++;
                i--;
            }
            else{
                ans += (long)(ver[j])*vp;
                hp++;
                j--;
            }
        }
        while(i >= 0){
             ans += (long)(hor[i])*hp;
                vp++;
                i--;
        }
        while(j >= 0){
            ans += (long)(ver[j])*vp;
                hp++;
                j--;
        }
        return ans;
    }
}
