#define ll long long
class Solution {
public:
    ll solveUtil(vector<int>& v1, vector<int>& v2, int m,int n){
        ll ans=0;
        ll varv1=0;
        ll v1p=1;
        ll varv2=0;
        ll v2p=1;
        while (varv1<m && varv2<n){
            if (v1[varv1]<=v2[varv2]) {
                ans+=v2[varv2]*v1p;
                v2p++;
                varv2++;
            } 
            else{
                ans+=v1[varv1]*v2p;
                v1p++;
                varv1++;
            }
        }
        while (varv1<m) {
            ans+=v1[varv1]*v2p;
            varv1++;
        }
        while (varv2<n){
            ans+=v2[varv2]*v1p;
            varv2++;
        }
        return ans;
    }
    ll solve(vector<int>& v1, vector<int>& v2, int m,int n){
        ll count = 0;
        sort(v2.rbegin(),v2.rend());
        sort(v1.rbegin(),v1.rend());
        count = solveUtil(v1,v2,m,n);
        return count;
    }
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        ll ans = solve(horizontalCut,verticalCut,m-1,n-1);
        return ans;
    }
};
