class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans = 0;
        priority_queue<int> vert, horz;
        for (int x : horizontalCut) horz.push(x);
        for (int x : verticalCut) vert.push(x);
        long long numvert = 1, numhorz = 1;
        while (!horz.empty() || !vert.empty()) {
            if (horz.empty() || (!vert.empty() && vert.top() >= horz.top())) {
                numvert++;
                ans += vert.top() * (m - horz.size());
                vert.pop();
            } else {
                numhorz++;
                ans += horz.top() * (n - vert.size());
                horz.pop();
            }
        }
        return ans;
    }
};
